INSERT INTO products_options_values (products_options_values_id, language_id, products_options_values_name) VALUES (0, 43, 'TEXT');
REPLACE INTO `configuration_language` (`configuration_title`, `configuration_key`, `configuration_language_id`, `configuration_description`, `last_modified`, `date_added`) VALUES ('Willkommenstext auf Startseite zeigen?', 'SHOW_CUSTOMER_GREETING', 43, 'Willkommenstext auf Startseite zeigen?<br />0= AUS<br />1= EIN', NULL, '2006-07-14 00:00:00'),
('&Uuml;berspringe Kategorien mit einem Artikel', 'SKIP_SINGLE_PRODUCT_CATEGORIES', 43, '&Uuml;berspringe Kategorien mit einem Artikel<br />Wenn true dann wird bei Klick auf die Kategorie gleich direkt die Artikelansicht angezeigt.<br />Default: True', NULL, '2006-07-14 00:00:00'),
('Sitemap - Mein Konto Link?', 'SHOW_ACCOUNT_LINKS_ON_SITE_MAP', 43, 'Soll ein Link f&uuml;r "Mein Konto" in der Sitemap generiert werden?<br /><br />Default: false', NULL, '2006-07-14 00:00:00'),
('Max. Anzahl PayPal IPN Transaktionen pro Seite', 'MAX_DISPLAY_SEARCH_RESULTS_PAYPAL_IPN', 43, 'Max. Anzahl PayPal IPN Transaktionen pro Seite<br />Default: 20', NULL, '2006-07-14 00:00:00'),
('Max. Anzahl W&ouml;rter f&uuml;r Metatag "description"', 'MAX_META_TAG_DESCRIPTION_LENGTH', 43, 'Max. Anzahl W&ouml;rter f&uuml;r description Metatag. Default 50:', NULL, '2006-07-14 00:00:00'),
('Artikelliste - Absteigend sortiert', 'PRODUCT_LIST_SORT_ORDER_DESCENDING', 43, 'Welcher Zeichen soll eine ansteigende Sortierung anzeigen?<br />Default = -', NULL, '2006-07-14 00:00:00'),
('Artikelliste - Aufsteigend sortiert', 'PRODUCT_LIST_SORT_ORDER_ASCENDING', 43, 'Welcher Zeichen soll eine aufsteigende Sortierung anzeigen?<br />Default = +', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Anzeigestatus - Sidebox', 'EZPAGES_STATUS_SIDEBOX', 43, 'Anzeige von EZ-Pages f&uuml;r den Sideboxbereich global sperren/freigeben <br />0= AUS<br />1= EIN<br />2= EIN (Nur Admin-IP: siehe Shopwartung)<br />Anmerkung: Seite kann nur von Admin gesehen werden', NULL, '2006-07-14 00:00:00'),
('CSS Schaltfl&auml;chen', 'IMAGE_USE_CSS_BUTTONS', 43, 'CSS Schaltfl&auml;chen<br />CSS Schaltfl&auml;chen anstelle von Bildern verwenden (GIF/JPG)?<br />CSS Schaltfl&auml;chen-Stile m&uuml;ssen in den Stylesheets definiert werden.', NULL, '2006-07-14 00:00:00'),
('Sprachauswahl', 'LANGUAGE_DEFAULT_SELECTOR', 43, 'Default Sprache wird durch Shop festgelegt oder die Browsereinstellung?<br /><br />Default: Shop', NULL, '2006-07-14 00:00:00'),
('Max. Anzahl EZ-Pages', 'MAX_DISPLAY_SEARCH_RESULTS_EZPAGE', 43, 'Maximale Anzahl EZ-Pages<br />20 = Default', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Anzeigestatus - Fu&szlig;zeilenbereich', 'EZPAGES_STATUS_FOOTER', 43, 'Anzeige von EZ-Pages f&uuml;r den Fu&szlig;zeilenbereich global sperren/freigeben <br />0= AUS<br />1= EIN<br />2= EIN (Nur Admin-IP: siehe Shopwartung)<br />Anmerkung: Seite kann nur von Admin gesehen werden', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Anzeigestatus - Kopfzeilenbereich', 'EZPAGES_STATUS_HEADER', 43, 'Anzeige von EZ-Pages f&uuml;r den Kopfzeilenbereich global sperren/freigeben <br />0= AUS<br />1= EIN<br />2= EIN (Nur Admin-IP: siehe Shopwartung)<br />Anmerkung: Seite kann nur von Admin gesehen werden', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Kapitel Inhaltsverzeichnis freischalten', 'EZPAGES_SHOW_TABLE_CONTENTS', 43, 'Kapitel Inhaltsverzeichnis f&uuml;r EZ-Pages freischalten?<br />0= AUS<br />1= EIN', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Fu&szlig;zeilen-Link Trennzeichen', 'EZPAGES_SEPARATOR_FOOTER', 43, 'EZ-Pages Fu&szlig;zeilen-Link Trennzeichen<br />Default = &amp;nbsp;::&amp;nbsp;', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Kopfzeilen-Link Trennzeichen', 'EZPAGES_SEPARATOR_HEADER', 43, 'EZ-Pages Kopfzeilen-Link Trennzeichen<br />Default = &amp;nbsp;::&amp;nbsp;', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Vor/Zur&uuml;ck Schaltfl&auml;chen', 'EZPAGES_SHOW_PREV_NEXT_BUTTONS', 43, 'Sollen Vor/Zur&uuml;ck Schaltflachen f&uuml;r EZ-Pages angezeigt werden?<br />0=AUS (keine Schaltfl&auml;chen)<br />1="Weiter"<br />2="Zur&uuml;ck/Weiter/Vor"<br /><br />Default Einstellung: 2.', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Linke Spalte f&uuml;r Seiten sperren', 'EZPAGES_DISABLE_LEFTCOLUMN_DISPLAY_LIST', 43, 'EZ-Pages "Seiten" f&uuml;r welche keine linke Seitenspalte angezeigt werden soll.<br />Einfach Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen &uuml;ber die EZ-Pages Maske ermittelt werden (Admin->Tools).<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Rechte Spalte f&uuml;r Seiten sperren', 'EZPAGES_DISABLE_RIGHTCOLUMN_DISPLAY_LIST', 43, 'EZ-Pages "Seiten" f&uuml;r welche keine rechte Seitenspalte angezeigt werden soll.<br />Einfach Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen &uuml;ber die EZ-Pages Maske ermittelt werden (Admin->Tools).<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Kopfzeile f&uuml;r Seiten sperren', 'EZPAGES_DISABLE_HEADER_DISPLAY_LIST', 43, 'EZ-Pages "Seiten" f&uuml;r welche keine Kopfzeile angezeigt werden soll.<br />Einfach Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen &uuml;ber die EZ-Pages Maske ermittelt werden (Admin->Tools).<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('EZ-Pages Fu&szlig;zeile f&uuml;r Seiten sperren', 'EZPAGES_DISABLE_FOOTER_DISPLAY_LIST', 43, 'EZ-Pages "Seiten" f&uuml;r welche keine Fu&szlig;zeile angezeigt werden soll.<br />Einfach Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen &uuml;ber die EZ-Pages Maske ermittelt werden (Admin->Tools).<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('e-Mail Absenderdomain verwenden?', 'EMAIL_SEND_MUST_BE_STORE', 43, 'Alle vom Mailserver verschickten E-Mails m&uuml;ssen eine Absenderadresse "FROM" haben?<br /><br />Dies wird oft verwendet um das verschicken von SPAM mails zu verhindern. Bei JA wird der Wert der Einstellung "e-Mail Absender" als "FROM" Adresse f&uuml;r alle ausgehenden Mails verwendet.', NULL, '2006-07-14 00:00:00'),
('Sitemaplink definieren', 'DEFINE_SITE_MAP_STATUS', 43, 'Sitemap Link/Text freischalten?<br />0= Link EIN, Text AUS<br />1= Link EIN, Text EIN<br />2= Link AUS, Text EIN<br />3= Link AUS, Text AUS', NULL, '2006-07-14 00:00:00'),
('Streaming download', 'DOWNLOAD_IN_CHUNKS', 43, 'Wenn download via redirect gesperrt ist und ihr PHP Speicherlimit < 8 MB ist, sollten Sie diese Einstellung verwenden da die Daten in kleineren Bl&ouml;cken an den Browser &uuml;bermittelt werden.<br /><br />Hat keine Bedeutung wenn Download via Redirect freigegeben ist.', NULL, '2006-07-14 00:00:00'),
('Kategorieicon Bildbreite - Artikeldetails', 'CATEGORY_ICON_IMAGE_WIDTH', 43, 'Breite in Pixel f&uuml;r das Kategorieicon auf der Artikeldetailseite', NULL, '2006-07-14 00:00:00'),
('W&auml;hrungssymbole f&uuml;r Text-Emails', 'CURRENCIES_TRANSLATIONS', 43, 'Welche W&auml;hrungssymbole sollen f&uuml;r Text-Emails konvertiert werden?<br />Default = &amp;pound;,EUR:&amp;euro;,EUR', NULL, '2006-07-14 00:00:00'),
('"Brotkr&uuml;mmel" Navigationpfad anzeigen', 'DEFINE_BREADCRUMB_STATUS', 43, 'Soll ein Navigationspfad angezeigt werden?<br />0= AUS<br />1= EIN', NULL, '2006-07-14 00:00:00'),
('Gutscheinlink definieren', 'DEFINE_DISCOUNT_COUPON_STATUS', 43, 'Gutscheinlink definieren (Link/Text)?<br />0= Link EIN, Text AUS<br />1= Link EIN, Text EIN<br />2= Link AUS, Text EIN<br />3= Link AUS, Text AUS', NULL, '2006-07-14 00:00:00'),
('Kategorieicon Bildh&ouml;he - Artikeldetails', 'CATEGORY_ICON_IMAGE_HEIGHT', 43, 'H&ouml;he in Pixel f&uuml;r das Kategorieicon auf der Artikeldetailseite', NULL, '2006-07-14 00:00:00'),
('Kategorie-Tabs Men&uuml; EIN/AUS', 'CATEGORIES_TABS_STATUS', 43, 'Kategorie-Tabs<br />Zeigt die Toplevel Kategorien unterhalb des Banners an. <br />0= Kategorie Tabs AUS<br />1= Kategorie Tabs EIN', NULL, '2006-07-14 00:00:00'),
('L&auml;nderliste - als Pulldownmen&uuml; anzeigen?', 'ACCOUNT_STATE_DRAW_INITIAL_DROPDOWN', 43, 'Soll die Eingabe des Landes durch eine Auswahlliste dargestellt werden?', NULL, '2006-07-14 00:00:00'),
('"Kunden Bewertung" : Benachrichtigung versenden', 'SEND_EXTRA_REVIEW_NOTIFICATION_EMAILS_TO_STATUS', 43, '0= Nein<br/>1= Ja', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailserver Port', 'EMAIL_SMTPAUTH_MAIL_SERVER_PORT', 43, 'SMTP Mailserver Port', NULL, '0001-01-01 00:00:00'),
('Fax Nummmer', 'ACCOUNT_FAX_NUMBER', 43, 'Soll das Feld "Faxnummer" in der Kontoerstellung und in den Kontoinformationen angezeigt werden?', NULL, '2006-07-14 00:00:00'),
('"Kunden Bewertung" : Kopie an diese e-Mail Adresse(n) versenden', 'SEND_EXTRA_REVIEW_NOTIFICATION_EMAILS_TO', 43, 'Eine Kopie an diese e-Mail Adresse(n) versenden, wenn eine Bewertung abgegeben wurde?<br/>Die Adressen m&uuml;ssen in diesem Format eingegeben werden: Name 1 &lt;email@adresse1&gt;, Name 2 &lt;email@adresse2&gt;\r\n', NULL, '0001-01-01 00:00:00'),
('Warenkorbsteuer anzeigen?', 'STORE_TAX_DISPLAY_STATUS', 43, 'Steuer im Warenkorb auch dann anzeigen wenn diese 0 ist?<br/>0= Aus<br/>1= Ein ', NULL, '0001-01-01 00:00:00'),
('Globale Attributfunktionen - Hinzuf&uuml;gen, Kopieren und L&ouml;schen   ', 'OPTION_NAMES_VALUES_GLOBAL_STATUS', 43, 'Globale Attributfunktionen (Attributname und Attributmerkmale) - Hinzuf&uuml;gen, Kopieren und L&ouml;schen<br/><br/>0= nicht verf&uuml;gbar<br/>1= verf&uuml;gbar', NULL, '0001-01-01 00:00:00'),
('Max. Anzahl Bestellpositionen / Auftrag (Liste im Adminbereich)', 'MAX_DISPLAY_RESULTS_ORDERS_DETAILS_LISTING', 43, 'Max. Anzahl Bestellpositionen / Auftrag (Liste im Adminbereich)<br/>0= unbegrenzt', NULL, '0001-01-01 00:00:00'),
('Max. Spaltenanzahl - Artikel zu Kategorien-Manager', 'MAX_DISPLAY_PRODUCTS_TO_CATEGORIES_COLUMNS', 43, 'Max. Spaltenanzahl - Artikel zu Kategorien-Manager<br/>3= default', NULL, '0001-01-01 00:00:00'),
('Max. Anzahl Gutscheine auf Reportseite', 'MAX_DISPLAY_SEARCH_RESULTS_DISCOUNT_COUPONS_REPORTS', 43, 'Max. Anzahl Gutscheine auf Reportseite', NULL, '0001-01-01 00:00:00'),
('Max. Anzahl Gutscheine pro Seite', 'MAX_DISPLAY_SEARCH_RESULTS_DISCOUNT_COUPONS', 43, 'Max. Anzahl Gutscheine pro Seite', NULL, '0001-01-01 00:00:00'),
('Umwandlung IP Adresse zu Hostname', 'SESSION_IP_TO_HOST_ADDRESS', 43, 'Soll die IP-Adresse auf einen Hostnamen umgewandelt werden?<br/><br/>Anmerkung: Auf manchen Systemen kann dies zu einem langsameren Session Start und E-Mailversand  kommen. ', NULL, '0001-01-01 00:00:00'),
('Mehrere Produkte in den Warenkorb &uuml;bernehmen?', 'PRODUCT_ALL_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che um mehrere Produkte mit einem Klick in den Warenkorb zu &uuml;bernehmen werden angezeigt.<br/><br/>0= AUS<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Artikelbeschreibung anzeigen', 'PRODUCT_LIST_DESCRIPTION', 43, 'Soll die Artikelbeschreibung angezeigt werden?<br/><br/>0= Aus<br/>oder z.B. 150 = es werden die ersten 150 Zeichen der Artikelbeschreibung angezeigt', NULL, '0001-01-01 00:00:00'),
('Mehrere Produkte in den Warenkorb &uuml;bernehmen?', 'PRODUCT_FEATURED_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che um mehrere Produkte mit einem Klick in den Warenkorb zu &uuml;bernehmen werden angezeigt.<br/><br/>0= AUS<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Mehrere Produkte in den Warenkorb &uuml;bernehmen?', 'PRODUCT_NEW_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che um mehrere Produkte mit einem Klick in den Warenkorb zu &uuml;bernehmen werden angezeigt.<br/><br/>0= AUS<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Mehrere Produkte in den Warenkorb &uuml;bernehmen?', 'PRODUCT_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che um mehrere Produkte mit einem Klick in den Warenkorb zu &uuml;bernehmen werden angezeigt.<br/><br/>0= AUS<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Kategorie/Artikel Sortierreihenfolge', 'CATEGORIES_PRODUCTS_SORT_ORDER', 43, 'Kategorie/Artikel Sortierreihenfolge<br/><br/>0= Kategorie/Artikel Sortierung/Name<br/>1= Kategorie/Artikel Name<br/>2= Artikelnummer<br/>3= Artikelmenge aufsteigend, Artikelname<br/>4= Artikelmenge abteigend, Artikelname<br/>5= Artikelpreis aufsteigend, Artikelname<br/>6= Artikelpreis absteigend, Artikelname<br/>', NULL, '0001-01-01 00:00:00'),
('Banner Anzeigengruppe - Sidebox banner_box_all', 'SHOW_BANNERS_GROUP_SET_ALL', 43, 'Welche Banneranzeigengruppe soll in der Sidebox "banner_box_all" angezeigt werden? F&uuml;r keine Gruppe Feld leer lassen!', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailserver Name', 'EMAIL_SMTPAUTH_MAIL_SERVER', 43, 'SMTP Mailserver f&uuml; Authentifizierung z.B. smtp.domain.com', NULL, '0001-01-01 00:00:00'),
('Erlaubte Dateierweiterungen f&uuml;r Datei-Upload', 'UPLOAD_FILENAME_EXTENSIONS', 43, 'Durch Komma getrennte Liste von Dateierweiterungen (ohne Punkt) welche f&uuml;r einen Datei-Upload zul&auml;ssig sind. z.B. jpg,jpeg,gif,png,eps,cdr,ai,pdf,tif,tiff,bmp,zip', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailbox Passwort', 'EMAIL_SMTPAUTH_PASSWORD', 43, 'Passwort f&uuml; SMTP Authentifizierung', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailbox Benutzer', 'EMAIL_SMTPAUTH_MAILBOX', 43, 'Wenn Sie f&uuml;r den Versand von E-Mails SMTP Authentifizierung verwenden m&uuml;ssen, dann geben Sie hier den Namen Ihres SMTP Benutzerkontos ein z.B. ich@domain.com ', NULL, '0001-01-01 00:00:00'),
('Warenkorb - Aktualisieren Schaltfl&auml;che', 'SHOW_SHOPPING_CART_UPDATE', 43, 'Wo soll die Aktualisieren Schaltfl&auml;che (Warenkorb) angezeigt werden?<br/><br/>1= Neben jedem Mengeneingabefeld<br/>2= Einmal unterhalb des Warenkorbes<br/>3= Neben jedem Mengeneingabefeld und unterhalb des Warenkorbes', NULL, '0001-01-01 00:00:00'),
('Warenkorb  verf&uuml;gbar - Artikel mit Read-Only Attributen', 'PRODUCTS_OPTIONS_TYPE_READONLY_IGNORED', 43, 'K&ouml;nnen Artikel mit nur Read-Only Attributen in den Warenkorb gelegt werden?<br/>0=NEIN<br/>1=JA', NULL, '0001-01-01 00:00:00'),
('Hersteller Liste - Produkt&uuml;berpr&uuml;fung', 'PRODUCTS_MANUFACTURERS_STATUS', 43, 'Hersteller wird nur dann in der Liste angezeigt wenn mindestens 1 Produkt von Ihm verf&uuml;gbar ist.<br/>0=AUS<br/>1=EIN<br/>Anmerkung: Ein aktivieren dieser Einstellung kann bei  Shops mit vielen Artikeln zu Performance-Einbu&szlig;en f&uuml;hren.', NULL, '0001-01-01 00:00:00'),
('Unterkategorien anzeigen?', 'SHOW_CATEGORIES_SUBCATEGORIES_ALWAYS', 43, 'Sollen Unterkategorien im Navigationsmen&uuml; angezeigt werden, wenn die Hauptkategorie selektiert ist?<br/>0=AUS<br/>1=EIN', NULL, '0001-01-01 00:00:00'),
('Anzahl weiterer gekaufter Artikel pro Zeile ', 'SHOW_PRODUCT_INFO_COLUMNS_ALSO_PURCHASED_PRODUCTS', 43, 'Anzahl weiterer gekaufter Artikel die pro Zeile angezeit werden sollen', NULL, '0001-01-01 00:00:00'),
('Proportionale Bilder f&uuml;r Artikel & Kategorien verwenden', 'PROPORTIONAL_IMAGES_STATUS', 43, 'Artikel und Kategoriebilder werden proportional verkleinert, falls die vorgegebenen Werte f&uuml;r H&ouml;he / Breite &uuml;berschritten werden. Anmerkung: Nicht verwenden wenn f&uuml;r H&ouml;he  bzw. Breite 0 verwendet wird.', NULL, '0001-01-01 00:00:00'),
('e-Mail Fehlermeldungen', 'EMAIL_FRIENDLY_ERRORS', 43, 'Gibt lesbare Fehlermeldungen aus falls der E-Mail Versand scheitert (true). Bei (false) werden auch PHP Fehler angezeigt . Diese Einstellung ist nur f&uuml;r die Fehlersuche gedacht!', NULL, '0001-01-01 00:00:00'),
('Max. Auftragsstatus f&uuml;r Download-Artikel', 'DOWNLOADS_CONTROLLER_ORDERS_STATUS_END', 43, 'Nur wenn der Auftragsstatus kleiner gleich dem eingegebenen Wert ist, k&ouml;nnen Download-Produkte heruntergeladen werden. Default: 4', NULL, '0001-01-01 00:00:00'),
('Default Auftragsstatus bei Bestellsumme 0', 'DEFAULT_ZERO_BALANCE_ORDERS_STATUS_ID', 43, 'Auftragsstatus der Bestellungen mit der Bestellsumme 0 zugewiesen werden soll', NULL, '0001-01-01 00:00:00'),
('Default e-Mail-Einstellung Kunde', 'ACCOUNT_EMAIL_PREFERENCE', 43, 'Standard Einstellung f&uuml;r e-Mails an Kunden<br/>0=Text<br/>1=HTML', NULL, '0001-01-01 00:00:00'),
('Eigene Seite f&uuml;r "Seite 2" verwenden', 'DEFINE_PAGE_2_STATUS', 43, 'Wollen Sie die eigene Seite f&uuml;r "Seite 2" verwenden?<br />0= NEIN<br />1= JA', '2004-11-20 11:14:04', '2004-11-20 11:14:04'),
