REPLACE INTO `configuration_language` (`configuration_title`, `configuration_key`, `configuration_language_id`, `configuration_description`, `last_modified`, `date_added`) VALUES ('"Vorheriger - N&auml;chster" Navigation: Sortierreihenfolge der Artikel', 'PRODUCT_INFO_PREVIOUS_NEXT_SORT', 43, 'Geben Sie hier an, wie die Artikel in der "Vorheriger - N&auml;chster" Navigation sortiert werden sollen<br />0= Artikel ID<br />1= Artikelname<br />2= Artikelnummer<br />3= Preis, Artikelname<br />4= Preis, Artikelnummer<br />5= Artikelname, Artikelnummer<br />6= Artikelsortierreihenfolge', '2004-11-20 11:14:04', '2004-11-20 11:14:04'),
