REPLACE INTO configuration_language (`configuration_title`, `configuration_key`, `configuration_language_id`, `configuration_description`, `last_modified`, `date_added`) VALUES ('Willkommenstext auf Startseite zeigen?', 'SHOW_CUSTOMER_GREETING', 43, 'Willkommenstext auf Startseite zeigen?<br />0= AUS<br />1= EIN', NULL, '2006-07-14 00:00:00'),
('&Uuml;berspringe Kategorien mit einem Artikel', 'SKIP_SINGLE_PRODUCT_CATEGORIES', 43, '&Uuml;berspringe Kategorien mit einem Artikel<br />Wenn true dann wird bei Klick auf die Kategorie gleich direkt die Artikelansicht, sprich der eine Artikel, angezeigt.<br />Standard: True', NULL, '2006-07-14 00:00:00'),
('Max. Anzahl PayPal IPN Transaktionen pro Seite', 'MAX_DISPLAY_SEARCH_RESULTS_PAYPAL_IPN', 43, 'Max. Anzahl der PayPal IPN Transaktionen pro Seite<br />Standard: 20', NULL, '2006-07-14 00:00:00'),
('Max. Anzahl W&ouml;rter f&uuml;r Metatag &quot;description&quot;', 'MAX_META_TAG_DESCRIPTION_LENGTH', 43, 'Max. Anzahl der W&ouml;rter f&uuml;r n Metatag &quot;description&quot;. Standard 50:', NULL, '2006-07-14 00:00:00'),
('Artikelliste - Absteigend sortiert', 'PRODUCT_LIST_SORT_ORDER_DESCENDING', 43, 'Welches/Welche Zeichen soll eine ansteigende Sortierung anzeigen?<br />Standard = -', NULL, '2006-07-14 00:00:00'),
('Artikelliste - Aufsteigend sortiert', 'PRODUCT_LIST_SORT_ORDER_ASCENDING', 43, 'Welches/Welche Zeichen soll eine aufsteigende Sortierung anzeigen?<br />Standard = +', NULL, '2006-07-14 00:00:00'),
('Sideboxen anzeigen', 'EZPAGES_STATUS_SIDEBOX', 43, 'Sollen die EZ-Pages Sideboxen global angezeigt werden?<br />0= NEIN<br />1= JA<br />2= JA (Nur Admin-IP: siehe Shopwartung)<br />Anmerkung: Seite kann nur von Admin gesehen werden', NULL, '2006-07-14 00:00:00'),
('CSS Schaltfl&auml;chen', 'IMAGE_USE_CSS_BUTTONS', 43, 'CSS Schaltfl&auml;chen<br />CSS Schaltfl&auml;chen anstelle von Image Buttons verwenden (GIF/JPG)?<br />CSS Schaltfl&auml;chen-Stile m&uuml;ssen in den Stylesheets definiert werden.', NULL, '2006-07-14 00:00:00'),
('Sprachauswahl', 'LANGUAGE_DEFAULT_SELECTOR', 43, 'Soll die Standardsprache durch den Shop oder durch die Browsereinstellung festgelegt werden?<br /><br />Standard: Shop', NULL, '2006-07-14 00:00:00'),
('Max. Anzahl EZ-Pages', 'MAX_DISPLAY_SEARCH_RESULTS_EZPAGE', 43, 'Maximale Anzahl EZ-Pages<br />20 = Standard', NULL, '2006-07-14 00:00:00'),
('Fu&szlig;zeile anzeigen', 'EZPAGES_STATUS_FOOTER', 43, 'Sollen die EZ-Pages Fu&szlig;zeilen global angezeigt werden?<br />0= NEIN<br />1= JA<br />2= JA (Nur Admin-IP: siehe Shopwartung)<br />Anmerkung: Seite kann nur von Admin gesehen werden', NULL, '2006-07-14 00:00:00'),
('Kopfzeile anzeigen', 'EZPAGES_STATUS_HEADER', 43, 'Sollen die EZ-Pages Kopfzeilen global angezeigt werden?<br />0= NEIN<br />1= JA<br />2= JA (Nur Admin-IP: siehe Shopwartung)<br />Anmerkung: Seite kann nur von Admin gesehen werden', NULL, '2006-07-14 00:00:00'),
('Inhaltsverzeichnis f&uuml;r Kapitel anzeigen', 'EZPAGES_SHOW_TABLE_CONTENTS', 43, 'Soll das EZ-Pages Inhaltsverzeichnis f&uuml;r Kapitel angezeigt werden?<br />0= NEIN<br />1= JA', NULL, '2006-07-14 00:00:00'),
('Trennzeichen f&uuml;r Links in Fu&szlig;zeile', 'EZPAGES_SEPARATOR_FOOTER', 43, 'Welche Trennzeichen sollen f&uuml;r Links in der EZ-Pages Fu&szlig;zeile angezeigt werden?<br />Standard = &amp;nbsp;::&amp;nbsp;', NULL, '2006-07-14 00:00:00'),
('Trennzeichen f&uuml;r Links in Kopfzeile', 'EZPAGES_SEPARATOR_HEADER', 43, 'Welche Trennzeichen sollen f&uuml;r Links in der EZ-Pages Kopfzeile angezeigt werden?<br />Standard = &amp;nbsp;::&amp;nbsp;', NULL, '2006-07-14 00:00:00'),
('Vor/Zur&uuml;ck Schaltfl&auml;chen', 'EZPAGES_SHOW_PREV_NEXT_BUTTONS', 43, 'Sollen Vor/Zur&uuml;ck Schaltflachen f&uuml;r EZ-Pages angezeigt werden?<br />0=NEIN (keine Schaltfl&auml;chen)<br />1=&quot;Weiter&quot;<br />2=&quot;Zur&uuml;ck/Weiter/Vor&quot;<br /><br />Standard = 2', NULL, '2006-07-14 00:00:00'),
('In diesen Seiten keine linke Spalte anzeigen', 'EZPAGES_DISABLE_LEFTCOLUMN_DISPLAY_LIST', 43, 'Geben Sie hier die &quot;Seiten&quot; der EZ-Pages an, in der keine linken Spalten (der Sideboxen) angezeigt werden sollen.<br />Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen in der EZ-Pages Ansicht &uuml;ber <em>Admin->Tools->EZ-Pages</em> ermittelt werden.<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('In diesen Seiten keine rechte Spalte anzeigen', 'EZPAGES_DISABLE_RIGHTCOLUMN_DISPLAY_LIST', 43, 'Geben Sie hier die &quot;Seiten&quot; der EZ-Pages an, in der keine rechten Spalten (der Sideboxen) angezeigt werden sollen.<br />Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen in der EZ-Pages Ansicht &uuml;ber <em>Admin->Tools->EZ-Pages</em> ermittelt werden.<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('In diesen Seiten keine Kopfzeile anzeigen', 'EZPAGES_DISABLE_HEADER_DISPLAY_LIST', 43, 'Geben Sie hier die &quot;Seiten&quot; der EZ-Pages an, in der keine Kopfzeile angezeigt werden sollen.<br />Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen in der EZ-Pages Ansicht &uuml;ber <em>Admin->Tools->EZ-Pages</em> ermittelt werden.<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('In diesen Seiten keine Fu&szlig;zeile anzeigen', 'EZPAGES_DISABLE_FOOTER_DISPLAY_LIST', 43, 'Geben Sie hier die &quot;Seiten&quot; der EZ-Pages an, in der keine Fu&szlig;zeile angezeigt werden sollen.<br />Seiten IDs durch Komma getrennt (ohne Leerzeichen) eingeben.<br />Seiten IDs k&ouml;nnen in der EZ-Pages Ansicht &uuml;ber <em>Admin->Tools->EZ-Pages</em> ermittelt werden.<br />z.B. 3,7<br />oder leer lassen.', NULL, '2006-07-14 00:00:00'),
('E-Mail Absenderdomain verwenden?', 'EMAIL_SEND_MUST_BE_STORE', 43, 'Alle vom Mailserver verschickten E-Mails m&uuml;ssen eine Absenderadresse &quot;FROM&quot; haben?<br /><br />Dies wird oft verwendet um das Verschicken von SPAM mails zu verhindern. Bei JA wird der Wert der Einstellung &quot;E-Mail Absender&quot; als &quot;FROM&quot; Adresse f&uuml;r alle ausgehenden Mails verwendet.', NULL, '2006-07-14 00:00:00'),
('Streaming download', 'DOWNLOAD_IN_CHUNKS', 43, 'Wenn Download via Redirect gesperrt ist und ihr PHP Speicherlimit < 8 MB ist, sollten Sie diese Einstellung verwenden da die Daten in kleineren Bl&ouml;cken an den Browser &uuml;bermittelt werden.<br /><br />Hat keine Bedeutung wenn Download via Redirect freigegeben ist.', NULL, '2006-07-14 00:00:00'),
('Kategorie: Bildbreite - Artikeldetails', 'CATEGORY_ICON_IMAGE_WIDTH', 43, 'Breite in Pixel f&uuml;r das Kategoriebild auf der Artikeldetailseite', NULL, '2006-07-14 00:00:00'),
('W&auml;hrungssymbole f&uuml;r Text-Emails', 'CURRENCIES_TRANSLATIONS', 43, 'Welche W&auml;hrungssymbole sollen f&uuml;r Text-Emails konvertiert werden?<br />Standard = &amp;pound;,EUR:&amp;euro;,EUR', NULL, '2006-07-14 00:00:00'),
('&quot;Brotkr&uuml;mel&quot; Navigationpfad anzeigen', 'DEFINE_BREADCRUMB_STATUS', 43, 'Soll ein Navigationspfad angezeigt werden?<br />0= AUS<br />1= EIN', NULL, '2006-07-14 00:00:00'),
('Aktionskupon Link definieren', 'DEFINE_DISCOUNT_COUPON_STATUS', 43, 'Aktionskupon Link definieren (Link/Text)?<br />0= Link EIN, Text AUS<br />1= Link EIN, Text EIN<br />2= Link AUS, Text EIN<br />3= Link AUS, Text AUS', NULL, '2006-07-14 00:00:00'),
('Kategorie: Bildh&ouml;he - Artikeldetails', 'CATEGORY_ICON_IMAGE_HEIGHT', 43, 'H&ouml;he in Pixel f&uuml;r das Kategoriebild auf der Artikeldetailseite', NULL, '2006-07-14 00:00:00'),
('Kategorie-Tabs Men&uuml; EIN/AUS', 'CATEGORIES_TABS_STATUS', 43, 'Kategorie-Tabs<br />Zeigt die Toplevel Kategorien unterhalb des Headers an. <br />0= Kategorie Tabs AUS<br />1= Kategorie Tabs EIN', NULL, '2006-07-14 00:00:00'),
('Zonen/Bundesl&auml;nder - als Pulldownmen&uuml; anzeigen?', 'ACCOUNT_STATE_DRAW_INITIAL_DROPDOWN', 43, 'Soll die Eingabe der Zone/des Bundeslandes durch eine Auswahlliste dargestellt werden?', NULL, '2006-07-14 00:00:00'),
('&quot;Kunden Bewertung&quot; : Benachrichtigung versenden', 'SEND_EXTRA_REVIEW_NOTIFICATION_EMAILS_TO_STATUS', 43, '0= Nein<br/>1= Ja', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailserver Port', 'EMAIL_SMTPAUTH_MAIL_SERVER_PORT', 43, 'SMTP Mailserver Port', NULL, '0001-01-01 00:00:00'),
('Faxnummer', 'ACCOUNT_FAX_NUMBER', 43, 'Soll das Feld &quot;Faxnummer&quot; in der Kontoerstellung und in den Kontoinformationen angezeigt werden?', NULL, '2006-07-14 00:00:00'),
('&quot;Kunden Bewertung&quot; : Kopie an diese e-Mail Adresse(n) versenden', 'SEND_EXTRA_REVIEW_NOTIFICATION_EMAILS_TO', 43, 'Eine Kopie an diese E-Mail Adresse(n) versenden, wenn eine Bewertung abgegeben wurde?<br/>Die Adressen m&uuml;ssen in diesem Format eingegeben werden: Name 1 &lt;email@adresse1&gt;, Name 2 &lt;email@adresse2&gt;\r\n', NULL, '0001-01-01 00:00:00'),
('Steuern auch bei 0% anzeigen?', 'STORE_TAX_DISPLAY_STATUS', 43, 'Steuer auch dann Anzeigen, wenn diese 0% betragen?<br/>0= NEIN<br/>1= JA ', NULL, '0001-01-01 00:00:00'),
('Globale Attributfunktionen - Hinzuf&uuml;gen, Kopieren und L&ouml;schen   ', 'OPTION_NAMES_VALUES_GLOBAL_STATUS', 43, 'Globale Attributfunktionen (Attributname und Attributmerkmale) - Hinzuf&uuml;gen, Kopieren und L&ouml;schen<br/><br/>0= nicht verf&uuml;gbar<br/>1= verf&uuml;gbar', NULL, '0001-01-01 00:00:00'),
('Max. Anzahl Bestellpositionen / Auftrag (Liste im Adminbereich)', 'MAX_DISPLAY_RESULTS_ORDERS_DETAILS_LISTING', 43, 'Max. Anzahl Bestellpositionen / Auftrag (Liste im Adminbereich)<br/>0= unbegrenzt', NULL, '0001-01-01 00:00:00'),
('Max. Spaltenanzahl - Artikel zu Kategorien-Manager', 'MAX_DISPLAY_PRODUCTS_TO_CATEGORIES_COLUMNS', 43, 'Max. Spaltenanzahl - Artikel zu Kategorien-Manager<br/>Standard = 3', NULL, '0001-01-01 00:00:00'),
('Max. Anzahl Aktionskupons auf Reportseite', 'MAX_DISPLAY_SEARCH_RESULTS_DISCOUNT_COUPONS_REPORTS', 43, 'Max. Anzahl Aktionskupons auf Reportseite', NULL, '0001-01-01 00:00:00'),
('Max. Anzahl Gutscheine pro Seite', 'MAX_DISPLAY_SEARCH_RESULTS_DISCOUNT_COUPONS', 43, 'Max. Anzahl Gutscheine pro Seite', NULL, '0001-01-01 00:00:00'),
('Umwandlung IP Adresse zu Hostname', 'SESSION_IP_TO_HOST_ADDRESS', 43, 'Soll die IP-Adresse in einen Hostnamen umgewandelt werden?<br/><br/>Anmerkung: Auf manchen Systemen kann dies zu einem langsameren Session Start und E-Mailversand f&uuml;hren. ', NULL, '0001-01-01 00:00:00'),
('Button &quot;Ausgew&auml;hlte Artikel in den Warenkorb&quot; anzeigen', 'PRODUCT_ALL_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che anzeigen, um mehrere ausgew&auml;hlte Artikel mit einem Klick in den Warenkorb zu &uuml;bernehmen?<br/><br/>0= NEIN<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Artikelbeschreibung anzeigen', 'PRODUCT_LIST_DESCRIPTION', 43, 'Soll die Artikelbeschreibung in der Artikelliste angezeigt werden?<br/><br/>0= Aus<br/>oder z.B. 150 = es werden die ersten 150 Zeichen der Artikelbeschreibung angezeigt', NULL, '0001-01-01 00:00:00'),
('Button &quot;Ausgew&auml;hlte Artikel in den Warenkorb&quot; anzeigen', 'PRODUCT_FEATURED_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che anzeigen, um mehrere ausgew&auml;hlte Artikel mit einem Klick in den Warenkorb zu &uuml;bernehmen?<br/><br/>0= NEIN<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Button &quot;Ausgew&auml;hlte Artikel in den Warenkorb&quot; anzeigen', 'PRODUCT_NEW_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che anzeigen, um mehrere ausgew&auml;hlte Artikel mit einem Klick in den Warenkorb zu &uuml;bernehmen?<br/><br/>0= NEIN<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Button &quot;Ausgew&auml;hlte Artikel in den Warenkorb&quot; anzeigen', 'PRODUCT_LISTING_MULTIPLE_ADD_TO_CART', 43, 'Eingabefelder und Schaltfl&auml;che anzeigen, um mehrere ausgew&auml;hlte Artikel mit einem Klick in den Warenkorb zu &uuml;bernehmen?<br/><br/>0= NEIN<br/>1= Oben<br/>2= Unten<br/>3= Oben und Unten', NULL, '0001-01-01 00:00:00'),
('Kategorie/Artikel Sortierung', 'CATEGORIES_PRODUCTS_SORT_ORDER', 43, 'Kategorie/Artikel Sortierung<br/><br/>0= Kategorie/Artikel Sortierung/Name<br/>1= Kategorie/Artikel Name<br/>2= Artikelnummer<br/>3= Artikelmenge aufsteigend, Artikelname<br/>4= Artikelmenge abteigend, Artikelname<br/>5= Artikelpreis aufsteigend, Artikelname<br/>6= Artikelpreis absteigend, Artikelname<br/>', NULL, '0001-01-01 00:00:00'),
('Banner Anzeigengruppe - Sidebox banner_box_all', 'SHOW_BANNERS_GROUP_SET_ALL', 43, 'Welche Banneranzeigengruppe soll in der Sidebox &quot;banner_box_all&quot; angezeigt werden? F&uuml;r keine Gruppe Feld leer lassen!', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailserver Name', 'EMAIL_SMTPAUTH_MAIL_SERVER', 43, 'SMTP Mailserver f&uuml; Authentifizierung z.B. smtp.domain.com', NULL, '0001-01-01 00:00:00'),
('Erlaubte Dateierweiterungen f&uuml;r Datei-Upload', 'UPLOAD_FILENAME_EXTENSIONS', 43, 'Durch Komma getrennte Liste von Dateierweiterungen (ohne Punkt) welche f&uuml;r einen Datei-Upload zul&auml;ssig sind. z.B. jpg,jpeg,gif,png,eps,cdr,ai,pdf,tif,tiff,bmp,zip', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailbox Passwort', 'EMAIL_SMTPAUTH_PASSWORD', 43, 'Passwort f&uuml; SMTP Authentifizierung', NULL, '0001-01-01 00:00:00'),
('SMTP Email - Mailbox Benutzer', 'EMAIL_SMTPAUTH_MAILBOX', 43, 'Wenn Sie f&uuml;r den Versand von E-Mails SMTP Authentifizierung verwenden m&uuml;ssen, dann geben Sie hier den Namen Ihres SMTP Benutzerkontos ein z.B. ich@domain.com ', NULL, '0001-01-01 00:00:00'),
('Warenkorb - Aktualisieren Schaltfl&auml;che', 'SHOW_SHOPPING_CART_UPDATE', 43, 'Wo soll die Aktualisieren Schaltfl&auml;che (Warenkorb) angezeigt werden?<br/><br/>1= Neben jedem Mengeneingabefeld<br/>2= Einmal unterhalb des Warenkorbes<br/>3= Neben jedem Mengeneingabefeld und unterhalb des Warenkorbes', NULL, '0001-01-01 00:00:00'),
('Warenkorb  verf&uuml;gbar - Artikel mit Read-Only Attributen', 'PRODUCTS_OPTIONS_TYPE_READONLY_IGNORED', 43, 'K&ouml;nnen Artikel mit nur Read-Only Attributen in den Warenkorb gelegt werden?<br/>0=NEIN<br/>1=JA', NULL, '0001-01-01 00:00:00'),
('Hersteller Liste - Produkt&uuml;berpr&uuml;fung', 'PRODUCTS_MANUFACTURERS_STATUS', 43, 'Hersteller wird nur dann in der Liste angezeigt wenn mindestens 1 Produkt von ihm verf&uuml;gbar ist.<br/>0=AUS<br/>1=EIN<br/>Anmerkung: Ein Aktivieren dieser Einstellung kann bei Shops mit vielen Artikeln zu Performance-Einbu&szlig;en f&uuml;hren.', NULL, '0001-01-01 00:00:00'),
('Unterkategorien anzeigen?', 'SHOW_CATEGORIES_SUBCATEGORIES_ALWAYS', 43, 'Sollen Unterkategorien im Navigationsmen&uuml; angezeigt werden, wenn die Hauptkategorie selektiert ist?<br/>0=AUS<br/>1=EIN', NULL, '0001-01-01 00:00:00'),
('Anzahl weiterer gekaufter Artikel pro Zeile ', 'SHOW_PRODUCT_INFO_COLUMNS_ALSO_PURCHASED_PRODUCTS', 43, 'Anzahl weiterer gekaufter Artikel die pro Zeile angezeigt werden sollen', NULL, '0001-01-01 00:00:00'),
('Proportionale Bilder f&uuml;r Artikel & Kategorien verwenden', 'PROPORTIONAL_IMAGES_STATUS', 43, 'Artikel und Kategoriebilder werden proportional verkleinert, falls die vorgegebenen Werte f&uuml;r H&ouml;he / Breite &uuml;berschritten werden. Anmerkung: Nicht verwenden wenn f&uuml;r H&ouml;he  bzw. Breite 0 verwendet wird.', NULL, '0001-01-01 00:00:00'),
('E-Mail Fehlermeldungen', 'EMAIL_FRIENDLY_ERRORS', 43, 'Gibt lesbare Fehlermeldungen aus falls der E-Mail Versand scheitert (true). Bei (false) werden auch PHP Fehler angezeigt . Diese Einstellung ist nur f&uuml;r die Fehlersuche gedacht!', NULL, '0001-01-01 00:00:00'),
('Max. Auftragsstatus f&uuml;r Download-Artikel', 'DOWNLOADS_CONTROLLER_ORDERS_STATUS_END', 43, 'Nur wenn der Auftragsstatus kleiner gleich dem eingegebenen Wert ist, k&ouml;nnen Download-Artikel heruntergeladen werden. Standard: 4', NULL, '0001-01-01 00:00:00'),
('Standard Auftragsstatus bei Bestellsumme 0', 'DEFAULT_ZERO_BALANCE_ORDERS_STATUS_ID', 43, 'Auftragsstatus der Bestellungen mit der Bestellsumme 0 zugewiesen werden soll', NULL, '0001-01-01 00:00:00'),
('e-Mail an Kunden im HTML Format senden', 'ACCOUNT_EMAIL_PREFERENCE', 43, 'Standard Einstellung f&uuml;r E-Mails an Kunden<br/>0=Text<br/>1=HTML', NULL, '0001-01-01 00:00:00'),
